/** @compileMembersOnly */
declare enum VideoFlags {
    V_PARAMMASK = 0x000000ff,
    V_SCALEPATCHSHIFT = 8,
    V_SCALEPATCHMASK = 0x00000300,
    V_SPACINGMASK = 0x00000c00,
    V_CHARCOLORSHIFT = 12,
    V_CHARCOLORMASK = 0x0000f000,
    V_ALPHASHIFT = 16,
    V_ALPHAMASK = 0x000f0000,

    V_NOSCALEPATCH = 0x00000100,
    V_SMALLSCALEPATCH = 0x00000200,
    V_MEDSCALEPATCH = 0x00000300,
    V_6WIDTHSPACE = 0x00000400, // early 2.1 style spacing, variable widths, 6 charact
    V_OLDSPACING = 0x00000800, // Old style spacing, 8 per character 4 per space
    V_MONOSPACE = 0x00000c00, // Don't do width checks on characters, all characters
    V_PURPLEMAP = 0x00001000,
    V_YELLOWMAP = 0x00002000,
    V_GREENMAP = 0x00003000,
    V_BLUEMAP = 0x00004000,
    V_REDMAP = 0x00005000,
    V_GRAYMAP = 0x00006000,
    V_ORANGEMAP = 0x00007000,
    V_SKYMAP = 0x00008000,
    V_LAVENDERMAP = 0x00009000,
    V_GOLDMAP = 0x0000a000,
    V_TEAMAP = 0x0000b000,
    V_STEELMAP = 0x0000c000,
    V_PINKMAP = 0x0000d000,
    V_BROWNMAP = 0x0000e000,
    V_PEACHMAP = 0x0000f000,
    V_10TRANS = 0x00010000,
    V_20TRANS = 0x00020000,
    V_30TRANS = 0x00030000,
    V_40TRANS = 0x00040000,
    V_TRANSLUCENT = 0x00050000, // TRANS50
    V_50TRANS = V_TRANSLUCENT,
    V_60TRANS = 0x00060000,
    V_70TRANS = 0x00070000,
    V_80TRANS = 0x00080000, // used to be V_8020TRANS
    V_90TRANS = 0x00090000,
    V_HUDTRANSHALF = 0x000d0000,
    V_HUDTRANS = 0x000e0000, // draw the hud translucent
    V_AUTOFADEOUT = 0x000f0000, // used by CECHOs, automatic fade out when almost over
    V_RETURN8 = 0x00100000, // 8 pixel return instead of 12
    V_OFFSET = 0x00200000, // account for offsets in patches
    V_ALLOWLOWERCASE = 0x00400000, // (strings only) allow fonts that have lowercase lett
    V_FLIP = 0x00400000, // (patches only) Horizontal flip
    V_SNAPTOTOP = 0x00800000, // for centering
    V_SNAPTOBOTTOM = 0x01000000, // for centering
    V_SNAPTOLEFT = 0x02000000, // for centering
    V_SNAPTORIGHT = 0x04000000, // for centering
    V_WRAPX = 0x08000000, // Don't clamp texture on X (for HW mode)
    V_WRAPY = 0x10000000, // Don't clamp texture on Y (for HW mode)
    V_NOSCALESTART = 0x20000000, // don't scale x, y, start coords
    V_SPLITSCREEN = 0x40000000,
    V_HORZSCREEN = 0x80000000,
}
